mapper.select:
SELECT a.id, a.parent_id, a.parent_ids, a.name, a.sort, a.href, a.target, a.icon, a.hidden_flag, a.permission, a.create_by, a.create_date, a.update_by, a.update_date, a.remarks, a.disable_flag, a.del_flag
FROM sys_menu a
WHERE
1=1

AND a.del_flag = #{delFlag}


AND a.disable_flag = #{disableFlag}

SELECT id, a.parent_id, a.parent_ids, a.name, a.sort, a.href, a.target, a.icon, a.hidden_flag, a.permission, a.create_by, a.create_date, a.update_by, a.update_date, a.remarks, a.disable_flag, a.del_flag FROM sys_menu a WHERE 1=1 AND a.parent_ids like '%${id},%'
AND a.del_flag = #{delFlag}
AND a.disable_flag = #{disableFlag}
