mapper.select:
SELECT id, parent_id, parent_ids, name, sort, sys_area_id, address, zip_code, master, phone, email, create_by, create_date, update_by, update_date, remarks, disable_flag, del_flag
FROM sys_office
WHERE 1=1

  AND del_flag = #{delFlag}


  AND disable_flag = #{disableFlag}

SELECT id, parent_id, parent_ids, name, sort, sys_area_id, address, zip_code, master, phone, email, create_by, create_date, update_by, update_date, remarks, disable_flag, del_flag
FROM sys_office
WHERE 1=1

  AND name like '%${name}%'


  AND parent_id = #{parentId}


  AND sys_area_id = #{sysAreaId}


  AND disable_flag = #{disableFlag}

SELECT id, parent_id, parent_ids, name, sort, sys_area_id, address, zip_code, master, phone, email, create_by, create_date, update_by, update_date, remarks, disable_flag, del_flag
FROM sys_office a
INNER JOIN sys_user b
ON a.id = b.sys_office_id
WHERE 1=1
b.sys_user_id = #{userId}

  AND a.del_flag = #{delFlag}


  AND a.disable_flag = #{disableFlag}

SELECT id, parent_id, parent_ids, name, sort, sys_area_id, address, zip_code, master, phone, email, create_by, create_date, update_by, update_date, remarks, disable_flag, del_flag FROM sys_office WHERE 1=1 AND parent_ids like '%${id},%'
AND del_flag = #{delFlag}
AND disable_flag = #{disableFlag}
