mapper.select:
SELECT id, office_id, parent_id, parent_ids, name, en_name, create_by, create_date, update_by, update_date,
remarks, disable_flag, del_flag
FROM sys_role
WHERE 1=1

    AND del_flag = #{delFlag}


    AND disable_flag = #{disableFlag}

SELECT id, office_id, parent_id, parent_ids, name, en_name, create_by, create_date, update_by, update_date,
remarks, disable_flag, del_flag
FROM sys_role
WHERE 1=1

    AND name like '%${name}%'


    AND parent_id = #{parentId}


    AND office_id = #{officeId}


    AND en_name = #{enName}


    AND disable_flag = #{disableFlag}

SELECT id, office_id, parent_id, parent_ids, name, en_name, create_by, create_date, update_by, update_date,
remarks, disable_flag, del_flag
FROM sys_role a
INNER JOIN sys_user_role b
ON a.id = b.sys_role_id
WHERE 1=1
b.sys_user_id = #{userId}

    AND a.del_flag = #{delFlag}


    AND a.disable_flag = #{disableFlag}

SELECT id, office_id, parent_id, parent_ids, name, en_name, create_by, create_date, update_by, update_date, remarks, disable_flag, del_flag FROM sys_role WHERE 1=1 AND parent_ids like '%${id},%'
AND del_flag = #{delFlag}
AND disable_flag = #{disableFlag}
