mapper.select:
SELECT id, office_id, parent_id, parent_ids, name, en_name, create_by, create_date, update_by, update_date, remarks, disable_flag, del_flag
FROM sys_role a
INNER JOIN sys_user_role b ON
a.id = b.sys_role_id
WHERE b.sys_user_id = #{sysUserId}

  AND a.del_flag = #{delFlag}


  AND a.disable_flag = #{disableFlag}

SELECT id, office_id, parent_id, parent_ids, name, en_name, create_by, create_date, update_by, update_date, remarks, disable_flag, del_flag
FROM sys_role a
WHERE
1=1

  AND a.del_flag = #{delFlag}


  AND a.disable_flag = #{disableFlag}

SELECT id, office_id, parent_id, parent_ids, name, en_name, create_by, create_date, update_by, update_date, remarks, disable_flag, del_flag FROM sys_role a WHERE 1=1 AND a.parent_ids like '%${id},%'
AND a.del_flag = #{delFlag}
AND a.disable_flag = #{disableFlag}
